  public class Person
  {
    private string name;
    private int age;
    private int weight;
    private int height;

    // Konstruktori, jossa vain nimi
    public Person(string name) : this(name, 0, 0, 0)
    {
    }

    // Konstruktori, jossa nimi ja ik\xE4
    public Person(string name, int age) :this(name, age, 0, 0)
